import React from 'react'

const Screen = (props) => {

    const data = props.data

    const getAttribute = (key, name) => {
        if (!data[key])
        return null

        return (

            <div className="pokedex__screen-row">
                <spam className="pokedex__screen-key">{name}</spam>
                <spam className="pokedex__screen-value">{data[key]}</spam>


            </div>
        )
    }

    return (
        <>
            {getAttribute("id", "Id")}
            {getAttribute("weight", "Id")}
            {getAttribute("height", "Id")}
        </>
    )

}

export default Screen;
