import React, { useState ,useEffect } from 'react';
import './Search.scss';
import axios from 'axios';
import Pokedex from '../Components/Pokedex';
import Gender from '../Components/Gender';

const Search = () => { 
    const [pokemonName, setPokemonName] = useState("");
    const [imgUrl, setImgUrl]= useState("");

    useEffect(() => {
        async function getPokemon(){
               const respone =  await axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`);
               const data = respone.data;
               setImgUrl(data.sprites.other.dream_world.front_default);
        }
       getPokemon();
    })



    return (
        <>
        <form className="search">  
           <input type="search" onChange={(event)=>{setPokemonName(event.target.value)}}/>
        </form>
        <Pokedex Img = {imgUrl}></Pokedex>
        <Gender Name = {pokemonName}/>
        </>
    );
}

export default Search;
