import React, { useState } from 'react';
import './Search.scss';
import axios from 'axios';

const Search = props => {

    const baseUrl = " https://pokeapi.co/api/v2/pokemon/"
    const [pokemonName, setPokemonName] = useState("")


    return (
        <form className="search" onSubmit={async ev => {
            ev.preventDefault()

            //try {
                axios.get(baseUrl, res).then((res) => {
                    this.setState({ total: res.data });
                }).catch(error => console.error(error));
                props.setData({
                    success: false,
                    name: pokemonName,


                })
            }

            //console.log(pokemonName)
        }>
            <input className='seacrh__input' placeholder="search Pokemon..." value={pokemonName} onChange={ev => setPokemonName(ev.target.value)} type="text"></input>
            <button className='search__button'>Go find all pokemon</button>
        </form>

    );
}

export default Search;
